mapper.update:
UPDATE cart SET quantity = quantity +
1 -1 0
WHERE user_id = #{userId} AND goods_id = #{goodsId}

UPDATE cart
SET ischosen = #{isChosen}
WHERE user_id = #{userId} AND goods_id = #{goodsId}

mapper.select:
SELECT quantity
FROM cart
WHERE user_id = #{userId} AND goods_id = #{goodsId}

SELECT SUM(ischosen) = COUNT(*)
FROM cart
WHERE user_id = #{userId};
